    private void jbsalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbsalvarActionPerformed

    
    if(jrativo.isSelected()){
    status=1;
    }
    if(jrinativo.isSelected()){
    status=0;    
    }
    int cod;
    
    objpes = new pessoas();
    preencher_objetos();
    
    try {
    
    IEDAO = new IncluirEditarDAO();
    
    if(acao==1) {
        
        cod=IEDAO.Incluir(objpes);
        jtcod.setText(String.valueOf(cod));
        JOptionPane.showMessageDialog(this, "Salvo com sucesso !");
        salvar();
    }
    
    if(acao==2){
    objpes.setPes_id(Integer.parseInt(jtcod.getText()));

    if(IEDAO.Editar(objpes)) {
    JOptionPane.showMessageDialog(this, "Alterado com Sucesso !");
    salvar();
    }  
    }
    
    }catch(SQLException error) {
    JOptionPane.showMessageDialog(this, "Opera√ß√£o N√£o Realizada" + error.getMessage());
    }
       
    }//GEN-LAST:event_jbsalvarActionPerformed


